legend( h, 'Data vs. TE', 'untitled fit 1', 'Location', 'NorthEast' );
text(10,10000,myfitinfo,'BackgroundColor',[.7 .9 .7]);
% Label axes
xlabel( 'TE (msec)' );
ylabel( 'Data' );
